import subscribe from "./decorators/subscribe";
import channel from "./decorators/channel";
import RizzoEvents from "./rizzo_events";
import { mark, measure } from "./utils/perf";

/**
 * Records some performance data about our component rendering.
 */
export default class PerfMonitor {
  get loadCritical() {
    return `mark_${RizzoEvents.LOAD_CRITICAL}`;
  }
  get loadBelow() {
    return `mark_${RizzoEvents.LOAD_BELOW}`;
  }
  constructor() {
    this.subscribe();
  }
  /**
   * Listens for when our critical code is loaded and creates a perf mark
   */
  @subscribe(RizzoEvents.LOAD_CRITICAL)
  @channel("events")
  loadCritical() {
    mark(this.loadCritical);
  }
  /**
   * Listens for when our below the fold code is loaded and creates a perf mark
   */
  @subscribe(RizzoEvents.LOAD_BELOW)
  loadBelow() {
    mark(this.loadBelow);

    measure(`mark_critical_to_below`, this.loadCritical, this.loadBelow);
    measure(`mark_critical`, "domContentLoadedEventEnd", this.loadCritical);
  }
}

import postal from "postal/lib/postal.lodash";
import events from "./rizzo_events";

/**
 * Rizzo thing
 */
export default class Rizzo {  
  constructor({ registry, logger, perf }) {
    this.registry = registry;
    this.logger = logger;
    this.perf = perf;
    this.events = events;
  }
  /**
   * Render a component
   * @param  {Component} Component The component to register
   * @param  {Object} options Options to pass into instance creation
   * @return {Object} Instance of the component
   * @example
   * rizzo.renderComponent(MastheadComponent, {});
   * 
   */
  renderComponent(Component, options = {}) {
    if (typeof options === "string") {
      options = {
        el: options
      };
    }

    let instance = this.registry.createInstanceOf(Component, options);

    return instance;
  }
  /**
   * Mark a rizzo event
   */
  event(name, data) {
    postal.channel("events").publish(name, data);
  }
}

/**
 * Rizzo global event names
 */
export default {
  LOAD_CRITICAL: "load.critical",
  LOAD_BELOW: "load.below"
}

