let getScrollbarWidth = function(){
  let html = document.getElementsByTagName("html")[0];
  let body = document.body;

  let withScroll = body.offsetWidth;
  let scrollWidth;

  let getWidth = function(){
    return new Promise((resolve) => {
      setTimeout(() => {
        html.style.overflowY = "scroll";
      }, 1);

      setTimeout(() => {
        html.style.overflowY = "hidden";

        scrollWidth = body.offsetWidth - withScroll;

        resolve(scrollWidth);
      }, 2);

      setTimeout(() => {
        html.style.overflowY = "";
      }, 3);
    });
  };

  return function(){
    return new Promise((resolve) => {
      withScroll = body.offsetWidth;

      if(scrollWidth){
        setTimeout(() => {
          resolve(scrollWidth);
        }, 1);
      } else {
        getWidth()
          .then((width) => {
            resolve(width);
          });
      }
    });
  };
}();

export default getScrollbarWidth;

let highlight = (node, re, nodeName, className) => {
  if (node.nodeType === 3) {
    let match = node.data.match(re);

    if (match) {
      let highlightElement = document.createElement(nodeName || "span");
      highlightElement.className = className || "highlight";

      let wordNode = node.splitText(match.index);
      wordNode.splitText(match[0].length);

      let wordClone = wordNode.cloneNode(true);
      highlightElement.appendChild(wordClone);
      wordNode.parentNode.replaceChild(highlightElement, wordNode);

      return 1; //skip added node in parent
    }
  } else if ((node.nodeType === 1 && node.childNodes) &&
             !/(script|style)/i.test(node.tagName) &&
             !(node.tagName === nodeName.toUpperCase() && node.className === className)) {
    for (let i = 0; i < node.childNodes.length; i++) {
      i += highlight(node.childNodes[i], re, nodeName, className);
    }
  }
  return 0;
};

export default function(el, words, options) {
  let settings = {
    className: "is-highlight",
    element: "span",
    caseSensitive: false,
    wordsOnly: false
  };

  $.extend(settings, options);

  if (words.constructor === String) {
    words = [ words ];
  }

  words = $.grep(words, function(word) {
    return word !== "";
  });
  words = $.map(words, function(word) {
    return word.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, "\\$&");
  });

  if (words.length === 0) {
    return el;
  }

  let flag = settings.caseSensitive ? "" : "i",
      pattern = "(" + words.join("|") + ")";

  if (settings.wordsOnly) {
    pattern = "\\b" + pattern + "\\b";
  }

  let re = new RegExp(pattern, flag);

  return $(el).each(() => highlight(el, re, settings.element, settings.className));
}

let mark = (name) => {
  if (window.performance && window.performance.mark) {
    window.performance.mark(name);
  }
};

let measure = (name, start, end) => {
  if (window.performance && window.performance.measure) {
    window.performance.measure(name, start, end);
    let entries;
    return (entries = window.performance.getEntriesByName(name))[entries.length - 1];
  }
};

export {
  mark, measure
};

/**
 * Preload images with jQuery
 * @example
 *   <img data-preload src="foo.jpg" />
 * @example
 *   <div data-preload="foo.jpg" style="background-image: url(foo.jpg)"></div>
 */
class $ImagePreloader {
  constructor({ el }) {
    let url = $(el).data("preload") || el.src;

    if (url) {
      new Image().src = url;
    }
  }
}

$("[data-preload]").each((i, el) => new $ImagePreloader({ el }));

const onTransitionEndEventNames = "transitionend webkitTransitionEnd oTransitionEnd otransitionend MSTransitionEnd";
// const onAnimationEndEventName = 'webkitAnimationEnd oanimationend msAnimationEnd animationend';

let waitForTransition = function($el, { fallbackTime = 10000 } = {}){
  return new Promise((resolve) => {

    let done = function(e){
      /*eslint no-use-before-define:0*/
      if(e && e.target !== $el.get(0)) {
        return;
      }

      resolve();

      $el.off(onTransitionEndEventNames, done);
      clearTimeout(fallBackTimer);
    };
    let fallBackTimer = setTimeout(done, fallbackTime);

    $el.on(onTransitionEndEventNames, done);
  });
};

module.exports = waitForTransition;

