  describe("component events", () => {
    it("should be observable", () => {
      class BaseComponent extends Component {

      }

      var base = new BaseComponent();

      var callCount = 0;
      var callback = function() { callCount++; };

      base.on("component.rendered", callback);

      expect(typeof base.on).to.be("function");
      expect(callCount).to.be(0);

      base.trigger("component.rendered");
      expect(callCount).to.be(1);
    });

    it("should be able to listen to arkham events", () => {
      var callCount = 0;

      class BaseComponent extends Component {
        initialize() {
          this.listenTo(Arkham, "app.ready", this.render);
        }
        render() {
          callCount++;
        }
      }

      new BaseComponent();
      Arkham.trigger("app.ready");

      expect(callCount).to.be(1);
    });
  });

  describe("getInitialState", () => {
    it("should parse an element", () => {
      class BaseComponent extends Component {

      }

      let base = new BaseComponent();
      base.$el.data("lpInitialFoo", { bar: "baz" });

      let state = base.getInitialState();

      expect(state.foo.bar).to.be("baz");
    });
  });
});

import publish from "../../../src/core/decorators/publish";
import postal from "postal/lib/postal.lodash";

let expect = require("expect.js");

describe("publish decorator", () => {
  it("should be a decorator", () => {
    expect(publish).to.be.ok();
  });

  it("should publish a message", () => {
    let definition = postal.channel("/").subscribe("my.message", (data) => {
      expect(data.foo).to.be("bar");
    });

    class Foo {
      @publish("my.message")
      myMessage() {
        return {
          foo: "bar"
        };
      }
    }

    let foo = new Foo();

    foo.myMessage();
    definition.unsubscribe();
  });

  it("should publish a message on a custom channel", () => {
    let definition = postal.channel("custom").subscribe("my.message", (data) => {
      expect(data.foo).to.be("bazinga");
    });

    class Foo {
      @publish("my.message", "custom")
      myMessage() {
        return {
          foo: "bazinga"
        };
      }
    }

    let foo = new Foo();

    let result = foo.myMessage();
    definition.unsubscribe();
    expect(result.foo).to.be("bazinga");
  });
});

import subscribe from "../../../src/core/decorators/subscribe";
import channel from "../../../src/core/decorators/channel";
import postal from "postal/lib/postal.lodash";

let expect = require("expect.js");

describe("subscribe decorator", () => {
  it("should be a decorator", () => {
    expect(subscribe).to.be.ok();
  });

  it("should subscribe to a topic", () => {
    let callCount = 0;
    class Foo {
      constructor() {
        this.subscribe();
      }
      @subscribe("my.message", "foo")
      myMessage(data, env, sub) {
        expect(data.foo).to.be("bar");
        callCount++;
        sub.unsubscribe();
      }
    }

    new Foo();

    postal.channel("foo").publish("my.message", { foo: "bar" });

    expect(callCount).to.be(1);
  });

  it("should subscribe to a topic with the channel set", () => {
    let callCount = 0;

    class Foo {
      constructor() {
        this.number = 2;
        this.subscribe();
      }
      @subscribe("my.other.message")
      @channel("foobar")
      myOtherMessage(data, env, sub) {
        this.number++;
        expect(data.foo).to.be("bazinga");
        callCount++;
        sub.unsubscribe();
      }
      @subscribe("my.other.message.2")
      myOtherMessage2(data, env, sub) {
        this.number++;
        expect(data.foo).to.be("bazinga");
        callCount++;
        sub.unsubscribe();
      }
      @subscribe("my.other.message.3")
      @channel("foobazinga")
      myOtherMessage3(data, env, sub) {
        this.number++;
        expect(data.foo).to.be("bazinga");
        callCount++;
        sub.unsubscribe();
      }
    }
    let foo = new Foo();

    postal.channel("foobar").publish("my.other.message", { foo: "bazinga" });
    postal.channel("foobar").publish("my.other.message.2", { foo: "bazinga" });
    postal.channel("foobazinga").publish("my.other.message.3", { foo: "bazinga" });

    expect(callCount).to.be(3);
    expect(foo.number).to.be(5);
  });
});

import CookieUtil from "../../../src/core/cookie_util";

let expect = require("expect.js");

/** @test {CookieUtil} */
describe("cookie util", () => {
  let cookie = null;
  before(() => {
    cookie = new CookieUtil({
      cookies: ""
    })
  });

  it("should set a cookie", () => {
    cookie.setCookie("testKey", "testValue");

    expect(cookie.cookies).to.be("testKey=testValue;path=/");
  });

  it("should set a cookie that expires", () => {
    cookie.setCookie("testKey", "testValue", 14);

    expect(/expires=/.test(cookie.cookies)).to.be(true);
  });
});
